	@Test
	public void test() {
		assertThat(solution.isPossible(nums), equalTo(expected));
	}
	
	class Solution {
		
	    public boolean isPossible(int[] a) {
	    	int[] b = f(a);
	        return r(b, 0);
	    }
	    
	    public boolean r(int[] a, int p) {
	    	for (; p < a.length && a[p] == 0; ++p) {
	    		;
	    	}
	    	
	    	if (p >= a.length) {
	    		return true;
	    	}
	    	
	    	int q;
	    	for (q = p; q < a.length && a[q] != 0; ++q) {
	    		;
	    	}
	    	return g(a, p, q) && r(a, q);
	    }
	    
	    public boolean g(int[] a, int p, int q) {
	    	if (q - p < 3) {
	    		return false;
	    	}
	    	while (p < q) {
	    		int k;
	    		for (k = p+1; k < q && a[k-1] <= a[k]; ++k) {
	    			;
	    		}
	    		if (k - p < 3) {
	    			return false;
	    		}
	    		for (int i = p, n = a[p]; i < k; ++i) {
	    			a[i] -= n;
	    		}
	    		
	    		for (; p < q && a[p] == 0; ++p) {
	    			;
	    		}
	    	}
	    	return true;
	    }
	    
	    
	    public int[] f(int[] a) {
	    	int min = a[0], max = a[a.length-1], m = Integer.MAX_VALUE;
	    	int[] b = new int[max-min+1];
	    	for (int i : a) {
	    		++b[i-min];
	    	}
	    	
	    	return b;
	    }
	}

